contracts/AIModelRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract AIModelRegistry is AccessControl, ReentrancyGuard {
    bytes32 public constant MODEL_VALIDATOR_ROLE =
        keccak256("MODEL_VALIDATOR_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    struct AIModel {
        string name;
        string version;
        string description;
        address creator;
        string modelHash; // IPFS hash
        string metadataURI;
        uint256 createdAt;
        uint256 lastUpdated;
        bool isActive;
        bool isPublic;
        uint256 usageCount;
        uint256 successRate; // Percentage 0-10000 (100.00%)
        uint256 totalPredictions;
        uint256 correctPredictions;
        uint256 stakingRequirement;
        mapping(string => uint256) performanceMetrics;
        mapping(address => bool) authorizedUsers;
        string[] categories;
        uint256 rewardPool;
        uint256 totalStaked;
    }

    struct ModelVersion {
        string version;
        string modelHash;
        uint256 timestamp;
        string changeLog;
        bool deprecated;
        uint256 performanceScore;
    }

    struct PredictionRecord {
        uint256 modelId;
        address predictor;
        string predictionHash;
        uint256 timestamp;
        bool validated;
        bool accurate;
        uint256 confidenceScore;
        uint256 rewardAmount;
        bytes32 evidenceHash;
    }

    struct ModelStake {
        address staker;
        uint256 amount;
        uint256 timestamp;
        bool active;
        uint256 rewards;
    }

    mapping(uint256 => AIModel) public models;
    mapping(uint256 => ModelVersion[]) public modelVersions;
    mapping(uint256 => PredictionRecord[]) public modelPredictions;
    mapping(uint256 => mapping(address => ModelStake)) public modelStakes;
    mapping(uint256 => address[]) public modelStakers;
    mapping(string => uint256[]) public modelsByCategory;
    mapping(address => uint256[]) public modelsByCreator;

    uint256 public modelCounter;
    uint256 public totalModelsStaked;
    uint256 public globalRewardPool;

    IERC20 public sToken;

    // Performance thresholds
    uint256 public constant MIN_SUCCESS_RATE = 6000; // 60%
    uint256 public constant MIN_PREDICTIONS_FOR_VALIDATION = 100;
    uint256 public constant PERFORMANCE_WINDOW = 30 days;
    uint256 public constant BASE_STAKING_REQUIREMENT = 1000 * 10 ** 18; // 1000 S tokens

    event ModelRegistered(
        uint256 indexed modelId,
        address indexed creator,
        string name
    );
    event ModelUpdated(uint256 indexed modelId, string newVersion);
    event PredictionSubmitted(
        uint256 indexed modelId,
        address indexed predictor,
        string predictionHash
    );
    event PredictionValidated(
        uint256 indexed modelId,
        uint256 predictionIndex,
        bool accurate
    );
    event ModelStaked(
        uint256 indexed modelId,
        address indexed staker,
        uint256 amount
    );
    event ModelUnstaked(
        uint256 indexed modelId,
        address indexed staker,
        uint256 amount
    );
    event RewardsDistributed(uint256 indexed modelId, uint256 totalRewards);
    event ModelDeactivated(uint256 indexed modelId, string reason);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function registerModel(
        string memory _name,
        string memory _version,
        string memory _description,
        string memory _modelHash,
        string memory _metadataURI,
        string[] memory _categories,
        bool _isPublic,
        uint256 _stakingRequirement
    ) external returns (uint256) {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_modelHash).length > 0, "Model hash cannot be empty");
        require(
            _stakingRequirement >= BASE_STAKING_REQUIREMENT,
            "Insufficient staking requirement"
        );

        uint256 modelId = modelCounter++;

        AIModel storage model = models[modelId];
        model.name = _name;
        model.version = _version;
        model.description = _description;
        model.creator = msg.sender;
        model.modelHash = _modelHash;
        model.metadataURI = _metadataURI;
        model.createdAt = block.timestamp;
        model.lastUpdated = block.timestamp;
        model.isActive = true;
        model.isPublic = _isPublic;
        model.categories = _categories;
        model.stakingRequirement = _stakingRequirement;

        // Add to version history
        modelVersions[modelId].push(
            ModelVersion({
                version: _version,
                modelHash: _modelHash,
                timestamp: block.timestamp,
                changeLog: "Initial version",
                deprecated: false,
                performanceScore: 0
            })
        );

        // Update category mappings
        for (uint256 i = 0; i < _categories.length; i++) {
            modelsByCategory[_categories[i]].push(modelId);
        }

        modelsByCreator[msg.sender].push(modelId);

        // Stake initial requirement
        require(
            sToken.transferFrom(msg.sender, address(this), _stakingRequirement),
            "Staking failed"
        );

        modelStakes[modelId][msg.sender] = ModelStake({
            staker: msg.sender,
            amount: _stakingRequirement,
            timestamp: block.timestamp,
            active: true,
            rewards: 0
        });

        modelStakers[modelId].push(msg.sender);
        models[modelId].totalStaked = _stakingRequirement;
        totalModelsStaked += _stakingRequirement;

        emit ModelRegistered(modelId, msg.sender, _name);
        emit ModelStaked(modelId, msg.sender, _stakingRequirement);

        return modelId;
    }

    function updateModel(
        uint256 _modelId,
        string memory _newVersion,
        string memory _newModelHash,
        string memory _changeLog
    ) external {
        require(_modelId < modelCounter, "Model does not exist");
        AIModel storage model = models[_modelId];
        require(model.creator == msg.sender, "Not model creator");
        require(model.isActive, "Model is not active");

        // Deprecate old version
        ModelVersion[] storage versions = modelVersions[_modelId];
        if (versions.length > 0) {
            versions[versions.length - 1].deprecated = true;
        }

        // Add new version
        versions.push(
            ModelVersion({
                version: _newVersion,
                modelHash: _newModelHash,
                timestamp: block.timestamp,
                changeLog: _changeLog,
                deprecated: false,
                performanceScore: 0
            })
        );

        model.version = _newVersion;
        model.modelHash = _newModelHash;
        model.lastUpdated = block.timestamp;

        emit ModelUpdated(_modelId, _newVersion);
    }

    function submitPrediction(
        uint256 _modelId,
        string memory _predictionHash,
        uint256 _confidenceScore,
        bytes32 _evidenceHash
    ) external nonReentrant {
        require(_modelId < modelCounter, "Model does not exist");
        AIModel storage model = models[_modelId];
        require(model.isActive, "Model is not active");
        require(_confidenceScore <= 100, "Invalid confidence score");

        // Check authorization for private models
        if (!model.isPublic) {
            require(
                model.authorizedUsers[msg.sender] ||
                    model.creator == msg.sender,
                "Not authorized"
            );
        }

        modelPredictions[_modelId].push(
            PredictionRecord({
                modelId: _modelId,
                predictor: msg.sender,
                predictionHash: _predictionHash,
                timestamp: block.timestamp,
                validated: false,
                accurate: false,
                confidenceScore: _confidenceScore,
                rewardAmount: 0,
                evidenceHash: _evidenceHash
            })
        );

        model.usageCount++;
        model.totalPredictions++;

        emit PredictionSubmitted(_modelId, msg.sender, _predictionHash);
    }

    function validatePrediction(
        uint256 _modelId,
        uint256 _predictionIndex,
        bool _accurate,
        uint256 _rewardAmount
    ) external onlyRole(ORACLE_ROLE) {
        require(_modelId < modelCounter, "Model does not exist");
        require(
            _predictionIndex < modelPredictions[_modelId].length,
            "Invalid prediction index"
        );

        PredictionRecord storage prediction = modelPredictions[_modelId][
            _predictionIndex
        ];
        require(!prediction.validated, "Already validated");

        prediction.validated = true;
        prediction.accurate = _accurate;
        prediction.rewardAmount = _rewardAmount;

        AIModel storage model = models[_modelId];

        if (_accurate) {
            model.correctPredictions++;

            // Distribute rewards
            if (_rewardAmount > 0 && model.rewardPool >= _rewardAmount) {
                model.rewardPool -= _rewardAmount;
                require(
                    sToken.transfer(prediction.predictor, _rewardAmount),
                    "Reward transfer failed"
                );
            }
        }

        // Update success rate
        model.successRate =
            (model.correctPredictions * 10000) /
            model.totalPredictions;

        // Check if model should be deactivated due to poor performance
        if (
            model.totalPredictions >= MIN_PREDICTIONS_FOR_VALIDATION &&
            model.successRate < MIN_SUCCESS_RATE
        ) {
            model.isActive = false;
            emit ModelDeactivated(_modelId, "Poor performance");
        }

        emit PredictionValidated(_modelId, _predictionIndex, _accurate);
    }

    function stakeOnModel(
        uint256 _modelId,
        uint256 _amount
    ) external nonReentrant {
        require(_modelId < modelCounter, "Model does not exist");
        require(_amount > 0, "Amount must be > 0");

        AIModel storage model = models[_modelId];
        require(model.isActive, "Model is not active");

        require(
            sToken.transferFrom(msg.sender, address(this), _amount),
            "Transfer failed"
        );

        ModelStake storage stake = modelStakes[_modelId][msg.sender];

        if (stake.amount == 0) {
            // New staker
            modelStakers[_modelId].push(msg.sender);
            stake.staker = msg.sender;
            stake.timestamp = block.timestamp;
            stake.active = true;
        }

        stake.amount += _amount;
        model.totalStaked += _amount;
        totalModelsStaked += _amount;

        emit ModelStaked(_modelId, msg.sender, _amount);
    }

    function unstakeFromModel(
        uint256 _modelId,
        uint256 _amount
    ) external nonReentrant {
        require(_modelId < modelCounter, "Model does not exist");

        ModelStake storage stake = modelStakes[_modelId][msg.sender];
        require(stake.amount >= _amount, "Insufficient staked amount");
        require(stake.active, "Stake is not active");

        // Model creator must maintain minimum stake
        if (msg.sender == models[_modelId].creator) {
            require(
                stake.amount - _amount >= models[_modelId].stakingRequirement,
                "Creator must maintain minimum stake"
            );
        }

        stake.amount -= _amount;
        models[_modelId].totalStaked -= _amount;
        totalModelsStaked -= _amount;

        if (stake.amount == 0) {
            stake.active = false;
        }

        require(sToken.transfer(msg.sender, _amount), "Transfer failed");

        emit ModelUnstaked(_modelId, msg.sender, _amount);
    }

    function distributeModelRewards(
        uint256 _modelId,
        uint256 _totalRewards
    ) external onlyRole(ORACLE_ROLE) {
        require(_modelId < modelCounter, "Model does not exist");
        require(_totalRewards > 0, "No rewards to distribute");

        AIModel storage model = models[_modelId];
        address[] storage stakers = modelStakers[_modelId];

        if (model.totalStaked == 0 || stakers.length == 0) {
            return;
        }

        for (uint256 i = 0; i < stakers.length; i++) {
            address staker = stakers[i];
            ModelStake storage stake = modelStakes[_modelId][staker];

            if (stake.active && stake.amount > 0) {
                uint256 reward = (_totalRewards * stake.amount) /
                    model.totalStaked;
                stake.rewards += reward;

                if (reward > 0) {
                    require(
                        sToken.transfer(staker, reward),
                        "Reward transfer failed"
                    );
                }
            }
        }

        emit RewardsDistributed(_modelId, _totalRewards);
    }

    function authorizeUser(uint256 _modelId, address _user) external {
        require(_modelId < modelCounter, "Model does not exist");
        require(models[_modelId].creator == msg.sender, "Not model creator");

        models[_modelId].authorizedUsers[_user] = true;
    }

    function deauthorizeUser(uint256 _modelId, address _user) external {
        require(_modelId < modelCounter, "Model does not exist");
        require(models[_modelId].creator == msg.sender, "Not model creator");

        models[_modelId].authorizedUsers[_user] = false;
    }

    // View functions
    function getModel(
        uint256 _modelId
    )
        external
        view
        returns (
            string memory name,
            string memory version,
            string memory description,
            address creator,
            string memory modelHash,
            bool isActive,
            bool isPublic,
            uint256 usageCount,
            uint256 successRate,
            uint256 totalStaked
        )
    {
        require(_modelId < modelCounter, "Model does not exist");
        AIModel storage model = models[_modelId];

        return (
            model.name,
            model.version,
            model.description,
            model.creator,
            model.modelHash,
            model.isActive,
            model.isPublic,
            model.usageCount,
            model.successRate,
            model.totalStaked
        );
    }

    function getModelPerformance(
        uint256 _modelId
    )
        external
        view
        returns (
            uint256 totalPredictions,
            uint256 correctPredictions,
            uint256 successRate,
            uint256 rewardPool
        )
    {
        require(_modelId < modelCounter, "Model does not exist");
        AIModel storage model = models[_modelId];

        return (
            model.totalPredictions,
            model.correctPredictions,
            model.successRate,
            model.rewardPool
        );
    }

    function getModelVersions(
        uint256 _modelId
    ) external view returns (ModelVersion[] memory) {
        require(_modelId < modelCounter, "Model does not exist");
        return modelVersions[_modelId];
    }

    function getModelsByCategory(
        string memory _category
    ) external view returns (uint256[] memory) {
        return modelsByCategory[_category];
    }

    function getModelsByCreator(
        address _creator
    ) external view returns (uint256[] memory) {
        return modelsByCreator[_creator];
    }

    function getUserStake(
        uint256 _modelId,
        address _user
    )
        external
        view
        returns (
            uint256 amount,
            uint256 timestamp,
            bool active,
            uint256 rewards
        )
    {
        ModelStake storage stake = modelStakes[_modelId][_user];
        return (stake.amount, stake.timestamp, stake.active, stake.rewards);
    }

    function getPredictionCount(
        uint256 _modelId
    ) external view returns (uint256) {
        return modelPredictions[_modelId].length;
    }

    function getPrediction(
        uint256 _modelId,
        uint256 _index
    )
        external
        view
        returns (
            address predictor,
            string memory predictionHash,
            uint256 timestamp,
            bool validated,
            bool accurate,
            uint256 confidenceScore,
            uint256 rewardAmount
        )
    {
        require(_index < modelPredictions[_modelId].length, "Invalid index");
        PredictionRecord storage prediction = modelPredictions[_modelId][
            _index
        ];

        return (
            prediction.predictor,
            prediction.predictionHash,
            prediction.timestamp,
            prediction.validated,
            prediction.accurate,
            prediction.confidenceScore,
            prediction.rewardAmount
        );
    }

    // Admin functions
    function addRewardPool(
        uint256 _modelId,
        uint256 _amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_modelId < modelCounter, "Model does not exist");
        require(
            sToken.transferFrom(msg.sender, address(this), _amount),
            "Transfer failed"
        );

        models[_modelId].rewardPool += _amount;
        globalRewardPool += _amount;
    }

    function emergencyDeactivateModel(
        uint256 _modelId,
        string memory _reason
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_modelId < modelCounter, "Model does not exist");
        models[_modelId].isActive = false;
        emit ModelDeactivated(_modelId, _reason);
    }
}

contracts/KnowledgeDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
contract KnowledgeDAO is ReentrancyGuard, Ownable, Pausable {
    IERC20 public sToken;

    struct AIContent {
        uint256 id;
        string contentHash; // IPFS hash
        address aiAgent;
        uint256 timestamp;
        uint256 totalStaked;
        uint256 positiveVotes;
        uint256 negativeVotes;
        bool finalized;
        uint256 rewardPool;
    }

    struct Vote {
        uint256 amount;
        bool positive;
        uint256 timestamp;
        bool claimed;
    }

    struct UserStats {
        uint256 totalStaked;
        uint256 accuracyScore;
        uint256 totalRewards;
        uint256 votingPower;
    }

    mapping(uint256 => AIContent) public aiContents;
    mapping(uint256 => mapping(address => Vote)) public votes;
    mapping(address => UserStats) public userStats;
    mapping(address => bool) public authorizedAI;

    uint256 public contentCounter;
    uint256 public minStakeAmount = 100 * 10 ** 18; // 100 S tokens
    uint256 public votingPeriod = 24 hours;
    uint256 public rewardPercentage = 10; // 10% of total stake as rewards

    event ContentSubmitted(
        uint256 indexed contentId,
        address indexed aiAgent,
        string contentHash
    );
    event VoteCast(
        uint256 indexed contentId,
        address indexed voter,
        uint256 amount,
        bool positive
    );
    event RewardsClaimed(address indexed user, uint256 amount);
    event ContentFinalized(uint256 indexed contentId, bool approved);

    constructor(address initialOwner, address _sToken) Ownable(initialOwner) {
        sToken = IERC20(_sToken);
    }

    modifier onlyAuthorizedAI() {
        require(authorizedAI[msg.sender], "Not authorized AI agent");
        _;
    }

    function authorizeAI(address _aiAgent) external onlyOwner {
        authorizedAI[_aiAgent] = true;
    }

    function submitContent(
        string memory _contentHash
    ) external onlyAuthorizedAI whenNotPaused {
        contentCounter++;

        aiContents[contentCounter] = AIContent({
            id: contentCounter,
            contentHash: _contentHash,
            aiAgent: msg.sender,
            timestamp: block.timestamp,
            totalStaked: 0,
            positiveVotes: 0,
            negativeVotes: 0,
            finalized: false,
            rewardPool: 0
        });

        emit ContentSubmitted(contentCounter, msg.sender, _contentHash);
    }

    function voteOnContent(
        uint256 _contentId,
        uint256 _amount,
        bool _positive
    ) external nonReentrant whenNotPaused {
        require(_amount >= minStakeAmount, "Insufficient stake amount");
        require(aiContents[_contentId].id != 0, "Content does not exist");
        require(!aiContents[_contentId].finalized, "Voting period ended");
        require(
            block.timestamp <= aiContents[_contentId].timestamp + votingPeriod,
            "Voting period expired"
        );
        require(votes[_contentId][msg.sender].amount == 0, "Already voted");

        require(
            sToken.transferFrom(msg.sender, address(this), _amount),
            "Transfer failed"
        );

        votes[_contentId][msg.sender] = Vote({
            amount: _amount,
            positive: _positive,
            timestamp: block.timestamp,
            claimed: false
        });

        aiContents[_contentId].totalStaked += _amount;

        if (_positive) {
            aiContents[_contentId].positiveVotes += _amount;
        } else {
            aiContents[_contentId].negativeVotes += _amount;
        }

        userStats[msg.sender].totalStaked += _amount;
        _updateVotingPower(msg.sender);

        emit VoteCast(_contentId, msg.sender, _amount, _positive);
    }

    function finalizeContent(uint256 _contentId) external {
        require(aiContents[_contentId].id != 0, "Content does not exist");
        require(!aiContents[_contentId].finalized, "Already finalized");
        require(
            block.timestamp > aiContents[_contentId].timestamp + votingPeriod,
            "Voting period not ended"
        );

        AIContent storage content = aiContents[_contentId];
        content.finalized = true;

        // Calculate reward pool (10% of total staked)
        content.rewardPool = (content.totalStaked * rewardPercentage) / 100;

        bool approved = content.positiveVotes > content.negativeVotes;
        emit ContentFinalized(_contentId, approved);
    }

    function claimRewards(uint256 _contentId) external nonReentrant {
        require(aiContents[_contentId].finalized, "Content not finalized");
        require(votes[_contentId][msg.sender].amount > 0, "No vote found");
        require(
            !votes[_contentId][msg.sender].claimed,
            "Rewards already claimed"
        );

        AIContent storage content = aiContents[_contentId];
        Vote storage userVote = votes[_contentId][msg.sender];

        bool approved = content.positiveVotes > content.negativeVotes;
        bool userWasCorrect = userVote.positive == approved;

        uint256 userReward = 0;
        uint256 stakeReturn = userVote.amount;

        if (userWasCorrect) {
            // Calculate proportional reward
            uint256 winningVotes = approved
                ? content.positiveVotes
                : content.negativeVotes;
            userReward = (content.rewardPool * userVote.amount) / winningVotes;

            // Update accuracy score
            userStats[msg.sender].accuracyScore += 10;
        } else {
            // Lose part of stake if wrong
            stakeReturn = (userVote.amount * 90) / 100; // 10% penalty
        }

        userVote.claimed = true;
        userStats[msg.sender].totalRewards += userReward;

        uint256 totalPayout = stakeReturn + userReward;
        require(sToken.transfer(msg.sender, totalPayout), "Transfer failed");

        emit RewardsClaimed(msg.sender, totalPayout);
    }

    function _updateVotingPower(address _user) internal {
        UserStats storage stats = userStats[_user];
        // Voting power based on accuracy and total staked
        stats.votingPower = (stats.accuracyScore * stats.totalStaked) / 1000;
    }

    function getContentDetails(
        uint256 _contentId
    ) external view returns (AIContent memory) {
        return aiContents[_contentId];
    }

    function getUserVote(
        uint256 _contentId,
        address _user
    ) external view returns (Vote memory) {
        return votes[_contentId][_user];
    }

    // Emergency functions
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function updateMinStakeAmount(uint256 _newAmount) external onlyOwner {
        minStakeAmount = _newAmount;
    }

    function updateVotingPeriod(uint256 _newPeriod) external onlyOwner {
        votingPeriod = _newPeriod;
    }
}

contracts/ReputationSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract ReputationSystem is Ownable, ReentrancyGuard {
    struct UserReputation {
        uint256 totalScore;
        uint256 contentScore;
        uint256 communityScore;
        uint256 aiValidationScore;
        uint256 lastUpdateTime;
        bool isActive;
        // Add missing fields
        uint256 votingAccuracy;
        uint256 stakingHistory;
        uint256 communityContribution;
        bool isInfluencer;
        uint256 followerCount;
        uint256 timeBasedDecay;
        uint256 lastUpdateTimestamp;
        mapping(string => uint256) expertiseAreas;
    }

    struct ReputationTransaction {
        address user;
        string actionType;
        int256 scoreChange;
        uint256 timestamp;
        bytes32 evidenceHash;
        bool aiValidated;
        uint256 confidenceScore;
    }

    mapping(address => UserReputation) public userReputations;
    mapping(bytes32 => ReputationTransaction) public reputationHistory;
    mapping(string => uint256) public actionTypeWeights;
    mapping(address => bool) public authorizedValidators;

    // Advanced reputation mechanics
    mapping(address => mapping(address => uint256)) public endorsements;
    mapping(address => address[]) public endorsedBy;
    mapping(string => address[]) public expertsByArea;

    uint256 public constant MAX_REPUTATION = 10000;
    uint256 public constant DECAY_RATE = 5; // 0.5% per month
    uint256 public constant INFLUENCER_THRESHOLD = 5000;
    uint256 public constant AI_VALIDATION_WEIGHT = 200; // 20% weight for AI validation

    bytes32[] public allTransactions;
    uint256 public totalUsers;

    event ReputationUpdated(
        address indexed user,
        string actionType,
        int256 scoreChange,
        uint256 newTotalScore
    );

    event InfluencerStatusChanged(address indexed user, bool isInfluencer);
    event ExpertiseAreaUpdated(
        address indexed user,
        string area,
        uint256 score
    );
    event EndorsementGiven(address indexed endorser, address indexed endorsed);
    event AIValidationReceived(
        address indexed user,
        uint256 confidenceScore,
        bytes32 evidenceHash
    );

    // ✅ Fix constructor for OpenZeppelin v5
    constructor(address initialOwner) Ownable(initialOwner) {
        // Initialize action type weights
        actionTypeWeights["accurate_vote"] = 100;
        actionTypeWeights["inaccurate_vote"] = 50;
        actionTypeWeights["content_creation"] = 150;
        actionTypeWeights["early_adoption"] = 75;
        actionTypeWeights["liquidity_provision"] = 125;
        actionTypeWeights["community_moderation"] = 200;
        actionTypeWeights["expert_analysis"] = 300;
        actionTypeWeights["ai_model_contribution"] = 250;
        actionTypeWeights["bug_report"] = 100;
        actionTypeWeights["governance_participation"] = 80;
    }

    modifier onlyAuthorizedOracle() {
        require(authorizedValidators[msg.sender], "Not authorized oracle");
        _;
    }

    function authorizeOracle(address _oracle) external onlyOwner {
        authorizedValidators[_oracle] = true;
    }

    function updateReputation(
        address _user,
        string memory _actionType,
        int256 _scoreModifier,
        bytes32 _evidenceHash,
        bool _isPositive
    ) external onlyAuthorizedOracle nonReentrant {
        require(_user != address(0), "Invalid user address");

        UserReputation storage userRep = userReputations[_user];

        // Apply time-based decay first
        _applyTimeDecay(_user);

        // Calculate score change
        uint256 baseWeight = actionTypeWeights[_actionType];
        require(baseWeight > 0, "Invalid action type");

        // ✅ Remove SafeMath and use native arithmetic
        int256 scoreChange = _isPositive
            ? int256((baseWeight * uint256(_scoreModifier)) / 100)
            : -int256((baseWeight * uint256(_scoreModifier)) / 100);

        // Apply the reputation change
        if (scoreChange > 0) {
            userRep.totalScore = userRep.totalScore + uint256(scoreChange);
            if (userRep.totalScore > MAX_REPUTATION) {
                userRep.totalScore = MAX_REPUTATION;
            }
        } else {
            uint256 decrease = uint256(-scoreChange);
            if (userRep.totalScore > decrease) {
                userRep.totalScore = userRep.totalScore - decrease;
            } else {
                userRep.totalScore = 0;
            }
        }

        // Update specific metrics based on action type
        _updateSpecificMetrics(_user, _actionType, _scoreModifier, _isPositive);

        // Update influencer status
        _updateInfluencerStatus(_user);

        // Record transaction
        bytes32 txHash = keccak256(
            abi.encodePacked(_user, _actionType, block.timestamp, _evidenceHash)
        );
        reputationHistory[txHash] = ReputationTransaction({
            user: _user,
            actionType: _actionType,
            scoreChange: scoreChange,
            timestamp: block.timestamp,
            evidenceHash: _evidenceHash,
            aiValidated: false,
            confidenceScore: 0
        });

        allTransactions.push(txHash);
        userRep.lastUpdateTimestamp = block.timestamp;

        emit ReputationUpdated(
            _user,
            _actionType,
            scoreChange,
            userRep.totalScore
        );
    }

    function submitAIValidation(
        bytes32 _transactionHash,
        uint256 _confidenceScore,
        bool _validationResult
    ) external onlyAuthorizedOracle {
        require(_confidenceScore <= 100, "Invalid confidence score");

        ReputationTransaction storage repTx = reputationHistory[
            _transactionHash
        ];
        require(repTx.user != address(0), "Transaction not found");
        require(!repTx.aiValidated, "Already validated");

        repTx.aiValidated = true;
        repTx.confidenceScore = _confidenceScore;

        // Apply AI validation bonus/penalty
        UserReputation storage userRep = userReputations[repTx.user];

        if (_validationResult && _confidenceScore >= 80) {
            // High confidence validation - bonus
            uint256 bonus = (AI_VALIDATION_WEIGHT * _confidenceScore) / 100;
            userRep.aiValidationScore = userRep.aiValidationScore + bonus;
            userRep.totalScore = userRep.totalScore + bonus;

            if (userRep.totalScore > MAX_REPUTATION) {
                userRep.totalScore = MAX_REPUTATION;
            }
        } else if (!_validationResult && _confidenceScore >= 80) {
            // High confidence rejection - penalty
            uint256 penalty = (AI_VALIDATION_WEIGHT * _confidenceScore) / 200; // Half penalty
            if (userRep.aiValidationScore > penalty) {
                userRep.aiValidationScore = userRep.aiValidationScore - penalty;
            } else {
                userRep.aiValidationScore = 0;
            }

            if (userRep.totalScore > penalty) {
                userRep.totalScore = userRep.totalScore - penalty;
            } else {
                userRep.totalScore = 0;
            }
        }

        emit AIValidationReceived(
            repTx.user,
            _confidenceScore,
            repTx.evidenceHash
        );
    }

    function endorseUser(address _user) external {
        require(_user != msg.sender, "Cannot endorse yourself");
        require(
            userReputations[msg.sender].totalScore >= 1000,
            "Insufficient reputation to endorse"
        );
        require(endorsements[msg.sender][_user] == 0, "Already endorsed");

        uint256 endorsementValue = userReputations[msg.sender].totalScore / 100; // 1% of endorser's reputation
        endorsements[msg.sender][_user] = endorsementValue;
        endorsedBy[_user].push(msg.sender);

        // Apply endorsement bonus
        UserReputation storage userRep = userReputations[_user];
        userRep.communityContribution =
            userRep.communityContribution +
            endorsementValue;
        userRep.totalScore = userRep.totalScore + (endorsementValue / 2); // 50% of endorsement value

        if (userRep.totalScore > MAX_REPUTATION) {
            userRep.totalScore = MAX_REPUTATION;
        }

        emit EndorsementGiven(msg.sender, _user);
    }

    function updateExpertiseArea(
        address _user,
        string memory _area,
        uint256 _score
    ) external onlyAuthorizedOracle {
        require(_score <= 100, "Invalid expertise score");

        UserReputation storage userRep = userReputations[_user];
        uint256 oldScore = userRep.expertiseAreas[_area];
        userRep.expertiseAreas[_area] = _score;

        // Update expert list for the area
        if (oldScore < 70 && _score >= 70) {
            expertsByArea[_area].push(_user);
        } else if (oldScore >= 70 && _score < 70) {
            _removeFromExpertList(_user, _area);
        }

        emit ExpertiseAreaUpdated(_user, _area, _score);
    }

    function _updateSpecificMetrics(
        address _user,
        string memory _actionType,
        int256 _scoreModifier,
        bool _isPositive
    ) internal {
        UserReputation storage userRep = userReputations[_user];
        bytes32 actionHash = keccak256(abi.encodePacked(_actionType));

        if (
            actionHash == keccak256(abi.encodePacked("accurate_vote")) &&
            _isPositive
        ) {
            userRep.votingAccuracy =
                userRep.votingAccuracy +
                uint256(_scoreModifier);
        } else if (
            actionHash == keccak256(abi.encodePacked("inaccurate_vote")) &&
            !_isPositive
        ) {
            if (userRep.votingAccuracy > uint256(-_scoreModifier)) {
                userRep.votingAccuracy =
                    userRep.votingAccuracy -
                    uint256(-_scoreModifier);
            } else {
                userRep.votingAccuracy = 0;
            }
        } else if (
            actionHash == keccak256(abi.encodePacked("content_creation")) &&
            _isPositive
        ) {
            userRep.communityContribution =
                userRep.communityContribution +
                uint256(_scoreModifier);
        }
    }

    function _applyTimeDecay(address _user) internal {
        UserReputation storage userRep = userReputations[_user];

        if (userRep.lastUpdateTimestamp == 0) {
            userRep.lastUpdateTimestamp = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - userRep.lastUpdateTimestamp;
        uint256 monthsElapsed = timeElapsed / 30 days;

        if (monthsElapsed > 0) {
            uint256 decayAmount = (userRep.totalScore *
                DECAY_RATE *
                monthsElapsed) / 1000;
            if (userRep.totalScore > decayAmount) {
                userRep.totalScore = userRep.totalScore - decayAmount;
            } else {
                userRep.totalScore = 0;
            }
            userRep.timeBasedDecay = userRep.timeBasedDecay + decayAmount;
        }
    }

    function _updateInfluencerStatus(address _user) internal {
        UserReputation storage userRep = userReputations[_user];
        bool wasInfluencer = userRep.isInfluencer;
        bool isInfluencer = userRep.totalScore >= INFLUENCER_THRESHOLD;

        if (wasInfluencer != isInfluencer) {
            userRep.isInfluencer = isInfluencer;
            emit InfluencerStatusChanged(_user, isInfluencer);
        }
    }

    function _removeFromExpertList(
        address _user,
        string memory _area
    ) internal {
        address[] storage experts = expertsByArea[_area];
        for (uint256 i = 0; i < experts.length; i++) {
            if (experts[i] == _user) {
                experts[i] = experts[experts.length - 1];
                experts.pop();
                break;
            }
        }
    }

    // View functions
    function getUserReputation(
        address _user
    )
        external
        view
        returns (
            uint256 totalScore,
            uint256 votingAccuracy,
            uint256 stakingHistory,
            uint256 communityContribution,
            uint256 aiValidationScore,
            bool isInfluencer,
            uint256 followerCount
        )
    {
        UserReputation storage userRep = userReputations[_user];
        return (
            userRep.totalScore,
            userRep.votingAccuracy,
            userRep.stakingHistory,
            userRep.communityContribution,
            userRep.aiValidationScore,
            userRep.isInfluencer,
            userRep.followerCount
        );
    }

    function getUserExpertise(
        address _user,
        string memory _area
    ) external view returns (uint256) {
        return userReputations[_user].expertiseAreas[_area];
    }

    function getExpertsByArea(
        string memory _area
    ) external view returns (address[] memory) {
        return expertsByArea[_area];
    }

    function getEndorsements(
        address _user
    ) external view returns (address[] memory) {
        return endorsedBy[_user];
    }

    function getTotalTransactions() external view returns (uint256) {
        return allTransactions.length;
    }

    function getRecentTransactions(
        uint256 _limit
    ) external view returns (bytes32[] memory) {
        uint256 length = allTransactions.length;
        uint256 returnLength = _limit > length ? length : _limit;
        bytes32[] memory recent = new bytes32[](returnLength);

        for (uint256 i = 0; i < returnLength; i++) {
            recent[i] = allTransactions[length - 1 - i];
        }

        return recent;
    }

    // Admin functions
    function updateActionTypeWeight(
        string memory _actionType,
        uint256 _weight
    ) external onlyOwner {
        actionTypeWeights[_actionType] = _weight;
    }

    function emergencyResetUser(address _user) external onlyOwner {
        UserReputation storage userRep = userReputations[_user];
        userRep.totalScore = 0;
        userRep.votingAccuracy = 0;
        userRep.stakingHistory = 0;
        userRep.communityContribution = 0;
        userRep.aiValidationScore = 0;
        userRep.isInfluencer = false;
    }
}

contracts/STokenERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract STokenERC20 is
    ERC20,
    ERC20Burnable,
    ERC20Pausable,
    Ownable,
    ReentrancyGuard
{
    uint256 public constant MAX_SUPPLY = 1000000000 * 10 ** 18; // 1B tokens
    uint256 public constant INITIAL_MINT = 100000000 * 10 ** 18; // 100M initial

    // Vesting and allocation tracking
    mapping(address => VestingSchedule) public vestingSchedules;
    mapping(address => bool) public minters;
    mapping(address => uint256) public stakingRewards;

    struct VestingSchedule {
        uint256 totalAmount;
        uint256 releasedAmount;
        uint256 startTime;
        uint256 cliffDuration;
        uint256 vestingDuration;
        bool revocable;
        bool revoked;
    }

    // Reward distribution for different activities
    struct RewardMultipliers {
        uint256 votingAccuracy; // Reward for accurate voting
        uint256 earlyAdoption; // Bonus for early users
        uint256 liquidityProvider; // LP rewards
        uint256 contentCreation; // AI content generation
        uint256 communityGov; // Governance participation
    }

    RewardMultipliers public rewardMultipliers;

    // Advanced staking mechanics
    struct StakingTier {
        uint256 minAmount;
        uint256 multiplier;
        uint256 lockPeriod;
        bool active;
    }

    mapping(uint256 => StakingTier) public stakingTiers;
    mapping(address => UserStakeInfo) public userStakes;

    struct UserStakeInfo {
        uint256 amount;
        uint256 tier;
        uint256 startTime;
        uint256 lastRewardClaim;
        uint256 rewardDebt;
        bool isLocked;
    }

    // Fee distribution system
    uint256 public totalFeesCollected;
    uint256 public burnRate = 200; // 2% burn on certain transactions
    uint256 public rewardPoolRate = 300; // 3% to reward pool
    uint256 public treasuryRate = 100; // 1% to treasury

    address public treasuryWallet;
    address public rewardPool;
    address public knowledgeDAO;

    // Events
    event VestingScheduleCreated(
        address indexed beneficiary,
        uint256 totalAmount,
        uint256 startTime
    );
    event TokensReleased(address indexed beneficiary, uint256 amount);
    event VestingRevoked(address indexed beneficiary);
    event StakingTierUpdated(
        uint256 indexed tier,
        uint256 minAmount,
        uint256 multiplier
    );
    event UserStaked(address indexed user, uint256 amount, uint256 tier);
    event UserUnstaked(address indexed user, uint256 amount);
    event RewardsDistributed(
        address indexed user,
        uint256 amount,
        string rewardType
    );

    // ✅ Fix constructor for OpenZeppelin v5
    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address initialOwner
    ) ERC20(name, symbol) Ownable(initialOwner) {
        _mint(initialOwner, initialSupply);
        _initializeStakingTiers();
    }

    function _initializeStakingTiers() internal {
        stakingTiers[1] = StakingTier(1000 * 10 ** 18, 100, 7 days, true); // Bronze: 1K tokens, 1x, 7 days
        stakingTiers[2] = StakingTier(10000 * 10 ** 18, 125, 30 days, true); // Silver: 10K tokens, 1.25x, 30 days
        stakingTiers[3] = StakingTier(50000 * 10 ** 18, 150, 90 days, true); // Gold: 50K tokens, 1.5x, 90 days
        stakingTiers[4] = StakingTier(250000 * 10 ** 18, 200, 180 days, true); // Platinum: 250K tokens, 2x, 180 days
        stakingTiers[5] = StakingTier(1000000 * 10 ** 18, 300, 365 days, true); // Diamond: 1M tokens, 3x, 365 days
    }

    modifier onlyMinter() {
        require(minters[msg.sender], "Not authorized minter");
        _;
    }

    modifier onlyKnowledgeDAO() {
        require(msg.sender == knowledgeDAO, "Only Knowledge DAO can call");
        _;
    }

    function setKnowledgeDAO(address _knowledgeDAO) external onlyOwner {
        knowledgeDAO = _knowledgeDAO;
    }

    function addMinter(address _minter) external onlyOwner {
        minters[_minter] = true;
    }

    function removeMinter(address _minter) external onlyOwner {
        minters[_minter] = false;
    }

    // Advanced minting with supply checks
    function mint(address to, uint256 amount) external onlyMinter {
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        _mint(to, amount);
    }

    // Vesting functionality
    function createVestingSchedule(
        address _beneficiary,
        uint256 _totalAmount,
        uint256 _cliffDuration,
        uint256 _vestingDuration,
        bool _revocable
    ) external onlyOwner {
        require(_beneficiary != address(0), "Invalid beneficiary");
        require(_totalAmount > 0, "Amount must be > 0");
        require(
            vestingSchedules[_beneficiary].totalAmount == 0,
            "Schedule exists"
        );

        vestingSchedules[_beneficiary] = VestingSchedule({
            totalAmount: _totalAmount,
            releasedAmount: 0,
            startTime: block.timestamp,
            cliffDuration: _cliffDuration,
            vestingDuration: _vestingDuration,
            revocable: _revocable,
            revoked: false
        });

        // Transfer tokens to contract for vesting
        _transfer(msg.sender, address(this), _totalAmount);

        emit VestingScheduleCreated(
            _beneficiary,
            _totalAmount,
            block.timestamp
        );
    }

    function releaseVestedTokens() external nonReentrant {
        VestingSchedule storage schedule = vestingSchedules[msg.sender];
        require(schedule.totalAmount > 0, "No vesting schedule");
        require(!schedule.revoked, "Schedule revoked");
        require(
            block.timestamp >= schedule.startTime + schedule.cliffDuration,
            "Cliff not reached"
        );

        uint256 vestedAmount = _calculateVestedAmount(msg.sender);
        uint256 releasableAmount = vestedAmount - schedule.releasedAmount;

        require(releasableAmount > 0, "No tokens to release");

        schedule.releasedAmount += releasableAmount;
        _transfer(address(this), msg.sender, releasableAmount);

        emit TokensReleased(msg.sender, releasableAmount);
    }

    function _calculateVestedAmount(
        address _beneficiary
    ) internal view returns (uint256) {
        VestingSchedule memory schedule = vestingSchedules[_beneficiary];

        if (block.timestamp < schedule.startTime + schedule.cliffDuration) {
            return 0;
        }

        if (block.timestamp >= schedule.startTime + schedule.vestingDuration) {
            return schedule.totalAmount;
        }

        uint256 timeVested = block.timestamp -
            schedule.startTime -
            schedule.cliffDuration;
        uint256 vestingTimeRemaining = schedule.vestingDuration -
            schedule.cliffDuration;

        return (schedule.totalAmount * timeVested) / vestingTimeRemaining;
    }

    // Advanced staking system
    function stake(
        uint256 _amount,
        uint256 _tier
    ) external nonReentrant whenNotPaused {
        require(_amount > 0, "Amount must be > 0");
        require(_tier >= 1 && _tier <= 5, "Invalid tier");
        require(stakingTiers[_tier].active, "Tier not active");
        require(
            _amount >= stakingTiers[_tier].minAmount,
            "Below minimum for tier"
        );
        require(userStakes[msg.sender].amount == 0, "Already staking");

        _transfer(msg.sender, address(this), _amount);

        userStakes[msg.sender] = UserStakeInfo({
            amount: _amount,
            tier: _tier,
            startTime: block.timestamp,
            lastRewardClaim: block.timestamp,
            rewardDebt: 0,
            isLocked: true
        });

        emit UserStaked(msg.sender, _amount, _tier);
    }

    function unstake() external nonReentrant {
        UserStakeInfo storage stakeInfo = userStakes[msg.sender];
        require(stakeInfo.amount > 0, "No stake found");

        StakingTier memory tier = stakingTiers[stakeInfo.tier];
        require(
            block.timestamp >= stakeInfo.startTime + tier.lockPeriod,
            "Still locked"
        );

        uint256 stakedAmount = stakeInfo.amount;

        // Calculate and distribute rewards before unstaking
        _distributeStakingRewards(msg.sender);

        // Reset stake info
        delete userStakes[msg.sender];

        // Return staked tokens
        _transfer(address(this), msg.sender, stakedAmount);

        emit UserUnstaked(msg.sender, stakedAmount);
    }

    function claimStakingRewards() external nonReentrant {
        require(userStakes[msg.sender].amount > 0, "No stake found");
        _distributeStakingRewards(msg.sender);
    }

    function _distributeStakingRewards(address _user) internal {
        UserStakeInfo storage stakeInfo = userStakes[_user];
        StakingTier memory tier = stakingTiers[stakeInfo.tier];

        uint256 timeStaked = block.timestamp - stakeInfo.lastRewardClaim;
        uint256 baseReward = (stakeInfo.amount * timeStaked * tier.multiplier) /
            (365 days * 10000);

        if (baseReward > 0) {
            stakeInfo.lastRewardClaim = block.timestamp;

            // Mint rewards (if within max supply)
            if (totalSupply() + baseReward <= MAX_SUPPLY) {
                _mint(_user, baseReward);
                emit RewardsDistributed(_user, baseReward, "staking");
            }
        }
    }

    // Activity-based reward distribution
    function distributeActivityReward(
        address _user,
        uint256 _baseAmount,
        string memory _activityType
    ) external onlyKnowledgeDAO {
        uint256 multiplier = _getActivityMultiplier(_activityType);
        uint256 rewardAmount = (_baseAmount * multiplier) / 100;

        if (totalSupply() + rewardAmount <= MAX_SUPPLY) {
            _mint(_user, rewardAmount);
            emit RewardsDistributed(_user, rewardAmount, _activityType);
        }
    }

    function _getActivityMultiplier(
        string memory _activityType
    ) internal view returns (uint256) {
        bytes32 activityHash = keccak256(abi.encodePacked(_activityType));

        if (activityHash == keccak256(abi.encodePacked("voting_accuracy"))) {
            return rewardMultipliers.votingAccuracy;
        } else if (
            activityHash == keccak256(abi.encodePacked("early_adoption"))
        ) {
            return rewardMultipliers.earlyAdoption;
        } else if (
            activityHash == keccak256(abi.encodePacked("liquidity_provider"))
        ) {
            return rewardMultipliers.liquidityProvider;
        } else if (
            activityHash == keccak256(abi.encodePacked("content_creation"))
        ) {
            return rewardMultipliers.contentCreation;
        } else if (
            activityHash == keccak256(abi.encodePacked("community_gov"))
        ) {
            return rewardMultipliers.communityGov;
        }

        return 100; // Default 1x multiplier
    }

    // ✅ Fix _update override for OpenZeppelin v5
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Pausable) {
        // Apply fees on transfers (excluding minting/burning)
        if (from != address(0) && to != address(0)) {
            if (
                from != address(this) &&
                to != address(this) &&
                from != knowledgeDAO &&
                to != knowledgeDAO
            ) {
                uint256 burnAmount = (amount * burnRate) / 10000;
                uint256 rewardAmount = (amount * rewardPoolRate) / 10000;
                uint256 treasuryAmount = (amount * treasuryRate) / 10000;

                uint256 totalFees = burnAmount + rewardAmount + treasuryAmount;
                uint256 transferAmount = amount - totalFees;

                // Call parent _update for the main transfer
                super._update(from, to, transferAmount);

                // Handle fees separately
                if (burnAmount > 0) {
                    super._update(from, address(0), burnAmount); // Burn
                }

                if (rewardAmount > 0 && rewardPool != address(0)) {
                    super._update(from, rewardPool, rewardAmount);
                }

                if (treasuryAmount > 0 && treasuryWallet != address(0)) {
                    super._update(from, treasuryWallet, treasuryAmount);
                }

                totalFeesCollected += totalFees;
                return;
            }
        }

        // Normal transfer
        super._update(from, to, amount);
    }

    // Admin functions
    function updateRewardMultipliers(
        uint256 _votingAccuracy,
        uint256 _earlyAdoption,
        uint256 _liquidityProvider,
        uint256 _contentCreation,
        uint256 _communityGov
    ) external onlyOwner {
        rewardMultipliers = RewardMultipliers({
            votingAccuracy: _votingAccuracy,
            earlyAdoption: _earlyAdoption,
            liquidityProvider: _liquidityProvider,
            contentCreation: _contentCreation,
            communityGov: _communityGov
        });
    }

    function updateStakingTier(
        uint256 _tier,
        uint256 _minAmount,
        uint256 _multiplier,
        uint256 _lockPeriod,
        bool _active
    ) external onlyOwner {
        require(_tier >= 1 && _tier <= 5, "Invalid tier");
        stakingTiers[_tier] = StakingTier(
            _minAmount,
            _multiplier,
            _lockPeriod,
            _active
        );
        emit StakingTierUpdated(_tier, _minAmount, _multiplier);
    }

    function updateFeeRates(
        uint256 _burnRate,
        uint256 _rewardPoolRate,
        uint256 _treasuryRate
    ) external onlyOwner {
        require(
            _burnRate + _rewardPoolRate + _treasuryRate <= 1000,
            "Total fees too high"
        ); // Max 10%
        burnRate = _burnRate;
        rewardPoolRate = _rewardPoolRate;
        treasuryRate = _treasuryRate;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // View functions
    function getVestingSchedule(
        address _beneficiary
    ) external view returns (VestingSchedule memory) {
        return vestingSchedules[_beneficiary];
    }

    function getVestedAmount(
        address _beneficiary
    ) external view returns (uint256) {
        return _calculateVestedAmount(_beneficiary);
    }

    function getUserStakeInfo(
        address _user
    ) external view returns (UserStakeInfo memory) {
        return userStakes[_user];
    }

    function getStakingTier(
        uint256 _tier
    ) external view returns (StakingTier memory) {
        return stakingTiers[_tier];
    }

    function calculateStakingRewards(
        address _user
    ) external view returns (uint256) {
        UserStakeInfo memory stakeInfo = userStakes[_user];
        if (stakeInfo.amount == 0) return 0;

        StakingTier memory tier = stakingTiers[stakeInfo.tier];
        uint256 timeStaked = block.timestamp - stakeInfo.lastRewardClaim;

        return
            (stakeInfo.amount * timeStaked * tier.multiplier) /
            (365 days * 10000);
    }
}

